'use strict';

//const { htmlTag } = require('hexo-util');

/**
* Bilibili tag
*
* Syntax:
*   {% bilibili video_id %}
*/

function bilibiliTag(args, content) {
/** 
  const src = 'https://www.bilibili.com/bideo/' + id;
  const iframeTag = htmlTag('iframe', {
    src,
    frameborder: '0',
    loading: 'lazy',
    allowfullscreen: true
  }, '');
  return htmlTag('div', { class: 'video-container' }, iframeTag, false);
*/
  var id = args[0];
  return `<div style="position: relative; width: 100%; height: 0; padding-bottom: 75%;"><iframe src="//player.bilibili.com/player.html?${id}&high_quality=1" scrolling="no" border="0" frameborder="no" framespacing="0" allowfullscreen="true" style="position: absolute; width: 100%; height: 100%; left: 0; top: 0;"></iframe></div>`;
}

module.exports = bilibiliTag;
